package samplejava;

public class LoopConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 //forloop 
		for(int i=1;i<=10;i++){ //initalization,conditional
			System.out.println(i);
		}
			
			//to print 10 t0 1
			System.out.println("...........");
			for(int k=10;k>=1;k--){
				System.out.println(k);
			}
		} 
		}
	

